             given=  fp = [5, 10, 3, 4];
             given=  np.interp(x, xp, fp, period=360);
             expected=
                "array([7.5, 5., 8.75, 6.25, 3., 3.25, 3.5, 3.75])";
            Assert.AreEqual(expected, given.repr);
            #endif
            // Complex interpolation:
            
            // >>> x = [1.5, 4.0]
            // >>> xp = [2,3,5]
            // >>> fp = [1.0j, 0, 2+3j]
            // >>> np.interp(x, xp, fp)
            // array([ 0.+1.j ,  1.+1.5j])
            // 
            
            #if TODO
             given=  x = [1.5, 4.0];
             given=  xp = [2,3,5];
             given=  fp = [1.0j, 0, 2+3j];
             given=  np.interp(x, xp, fp);
             expected=
                "array([ 0.+1.j ,  1.+1.5j])";
            Assert.AreEqual(expected, given.repr);
            #endif
        }
        
    }
